import { call, put, takeEvery } from "redux-saga/effects";
import { addUser } from "../store/userSlice";

function* workUsers() {
    const user = yield call(() => fetch(" http://ip.jsontest.com/"));
    const formattedUser = yield user.json();
    const format = yield formattedUser.slice(0, 2);
    yield put(addUser(format));
}

function* watchUsers() {
    yield takeEvery("users/addUser", workUsers);
}

export default watchUsers;
